mapper.select:
SELECT r.record_id AS record_id, r.description AS description, r.photo_path AS photo_path, a.alcohol_id AS alcohol_id, a.name AS alcohol_name, a.production_location AS production_location, a.production_latitude AS production_latitude, a.production_longitude AS production_longitude, a.alcohol_tag AS alcohol_tag, ab.name AS brand_name FROM record AS r LEFT JOIN (SELECT alcohol_id, brand_id, name, production_location, production_latitude, production_longitude, alcohol_tag FROM alcohol) AS a ON r.alcohol_id = a.alcohol_id LEFT JOIN alcohol_brand AS ab ON a.brand_id = ab.brand_id WHERE r.member_id = #{memberId}
AND r.record_id < #{recordSearchParamDto.cursor}
AND (a.name LIKE CONCAT('%', #{recordSearchParamDto.keyword}, '%') OR ab.name LIKE CONCAT('%', #{recordSearchParamDto.keyword}, '%') OR r.description LIKE CONCAT('%', #{recordSearchParamDto.keyword}, '%'))
ORDER BY r.record_id DESC
LIMIT #{recordSearchParamDto.limit}

SELECT r.member_id AS member_id, r.record_id AS record_id, r.description AS description, r.photo_path AS photo_path, a.alcohol_id AS alcohol_id, a.name AS alcohol_name, a.production_location AS production_location, a.production_latitude AS production_latitude, a.production_longitude AS production_longitude, a.alcohol_tag AS alcohol_tag, ab.name AS brand_name FROM record AS r LEFT JOIN ( SELECT alcohol_id, brand_id, name, production_location, production_latitude, production_longitude, alcohol_tag FROM alcohol ) AS a ON r.alcohol_id = a.alcohol_id LEFT JOIN alcohol_brand AS ab ON a.brand_id = ab.brand_id
WHERE r.record_id < #{recordSearchParamDto.cursor}
ORDER BY r.record_id DESC
LIMIT #{recordSearchParamDto.limit}
